import { SingletonApolloClient } from '../singleton/client';

export const readDataGQL = async(variables, query)=>{
    const client = new SingletonApolloClient().getClient()
    try{
        let res = await client
            .query({
                variables: variables,
                query: query
            })
        client.writeQuery({
            variables: variables,
            query: query,
            data: res.data,
        });
        return res.data
    } catch(err){
        return client.readQuery({
            variables: variables,
            query: query,
        });
    }
}
