import { makeStyles } from '@material-ui/core/styles';
export default makeStyles(theme => ({
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
    },
    root: {
        flexGrow: 1,
    },
    title: {
        flexGrow: 1,
    },
    searchD: {
        position: 'fixed',
        top: 6,
        right: 6
    },
    searchM: {
        position: 'fixed',
        top: 0,
        right: 0,
        height: '56px',
        width: '100vw'
    },
    textField: {
        width: 'calc(100% - 20px)',
        margin: 10,
    },
    searchField: {
        width: 'calc(100% - 20px)',
        margin: 10,
    },
}))
