import React, { useState, useRef } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import * as mini_dialogActions from '../../redux/actions/mini_dialog'
import dialogContentStyle from '../../src/styleMUI/dialogContent'
import { Map, YMaps, Placemark } from 'react-yandex-maps';
import Fab from '@material-ui/core/Fab';
import GpsFixed from '@material-ui/icons/GpsFixed';
import * as snackbarActions from '../../redux/actions/snackbar'
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import Confirmation from './Confirmation'

const Geo =  React.memo(
    (props) =>{
        const { showSnackBar } = props.snackbarActions;
        const { isMobileApp } = props.app;
        const { showMiniDialog, setMiniDialog } = props.mini_dialogActions;
        const { classes, geo, name, idx, setAddressGeo } = props;
        let [newGeo, setNewGeo] = useState(geo?geo:'42.8700000, 74.5900000');
        let getGeo = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition((position)=>{
                    setNewGeo(position.coords.latitude+', '+position.coords.longitude)
                });
            } else {
                showSnackBar('Геолокация не поддерживается')
            }
        }
        let dragend = (e) => {
            let geo = e.get('target').geometry.getCoordinates()
            setNewGeo(geo[0]+', '+geo[1])
        }
        let [load, setLoad] = useState(true);
        let nameRef = useRef();
        nameRef.current = setLoad
        return (
            <YMaps onLoad={()=>{console.log('load1')}}>
                <div className={classes.column}>
                    <div style={{height: window.innerHeight-128, width: window.innerWidth-48}}>
                        {
                            load?<CircularProgress/>:null
                        }
                        <div style={{display: load?'none':'block'}}>
                            <Map onLoad={()=>{console.log('load2')}} height={window.innerHeight-128} width={window.innerWidth-48} defaultState={{ center: [42.8700000, 74.5900000], zoom: 12 }} >
                                <Placemark
                                    onDragEnd={dragend}
                                    options={{draggable: true, iconColor: '#ffb300'}} properties={{iconCaption: name}} geometry={newGeo.split(', ')} />
                            </Map>
                        </div>
                    </div>
                    <center>
                        <Button variant='contained' color='primary' onClick={async()=>{
                            const action = async() => {
                                await setAddressGeo(geo, idx)
                                showMiniDialog(false);
                            }
                            setMiniDialog('Вы уверенны?', <Confirmation action={action}/>)
                        }} className={classes.button}>
                            Сохранить
                        </Button>
                        <Button variant='contained' color='secondary' onClick={()=>{showMiniDialog(false);}} className={classes.button}>
                            Закрыть
                        </Button>
                    </center>
                </div>
                <Fab color='primary' aria-label='Найти геолокацию' className={classes.fabGeo} onClick={getGeo}>
                    <GpsFixed/>
                </Fab>
            </YMaps>
        );
    }
)

function mapStateToProps (state) {
    return {
        mini_dialog: state.mini_dialog,
        app: state.app
    }
}

function mapDispatchToProps(dispatch) {
    return {
        snackbarActions: bindActionCreators(snackbarActions, dispatch),
        mini_dialogActions: bindActionCreators(mini_dialogActions, dispatch)
    }
}

Geo.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(dialogContentStyle)(Geo));
