import { SingletonApolloClient } from '../singleton/client';

export const readDataGQL = async({variables, query})=>{
    const client = new SingletonApolloClient().getClient()
    try{
        let res = await client
            .query({
                variables: variables,
                query: query
            })
        console.log(res.data)
        client.writeFragment({
            id: JSON.stringify(variables),
            fragment: query,
            data: res.data,
        });
        return res.data
    } catch(err){
        let res = client.readFragment({
            id: JSON.stringify(variables),
            fragment: query,
        });
        console.log(res)
    }
}
