import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import * as mini_dialogActions from '../../redux/actions/mini_dialog'
import dialogContentStyle from '../../src/styleMUI/dialogContent'
import { Map, YMaps, Placemark } from 'react-yandex-maps';
import Fab from '@material-ui/core/Fab';
import GpsFixed from '@material-ui/icons/GpsFixed';
import * as snackbarActions from '../../redux/actions/snackbar'

const Geo =  React.memo(
    (props) =>{
        const { showSnackBar } = props.snackbarActions;
        const { isMobileApp } = props.app;
        const { showMiniDialog, setMiniDialog } = props.mini_dialogActions;
        const { classes, geo, name } = props;
        const width = isMobileApp? (window.innerWidth-126) : 500;
        let [newGeo, setNewGeo] = useState(geo?geo:'42.8700000, 74.5900000');
        let getGeo = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition((position)=>{
                    setNewGeo(position.coords.latitude+', '+position.coords.longitude)
                });
            } else {
                showSnackBar('Геолокация не поддерживается')
            }
        }
        let dragend = (e) => {
            let geo = e.get('target').geometry.getCoordinates()
            setNewGeo(geo[0]+', '+geo[1])
        }
        console.log(newGeo)
        return (
            <YMaps>
                <div className={classes.column} style={{width: width}}>
                    <Map height={width} width={width} defaultState={{ center: [42.8700000, 74.5900000], zoom: 12 }} >
                        <Placemark
                            onDragEnd={dragend}
                            options={{draggable: true, iconColor: '#ffb300'}} properties={{iconCaption: name}} geometry={newGeo.split(', ')} />
                    </Map>
                </div>
                <Fab color='primary' aria-label='Найти геолокацию' className={classes.fab} onClick={getGeo}>
                    <GpsFixed/>
                </Fab>
            </YMaps>
        );
    }
)

function mapStateToProps (state) {
    return {
        mini_dialog: state.mini_dialog,
        app: state.app
    }
}

function mapDispatchToProps(dispatch) {
    return {
        snackbarActions: bindActionCreators(snackbarActions, dispatch),
        mini_dialogActions: bindActionCreators(mini_dialogActions, dispatch)
    }
}

Geo.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(dialogContentStyle)(Geo));
